public class Solution { public ListNode AddTwoNumbers(ListNode l1, ListNode l2) { ListNode dummy = new ListNode(-1); ListNode tail = dummy; int carry = 0; while (l1 != null || l2 != null || carry != 0) { int sum = (l1?.val ?? 0) + (l2?.val ?? 0) + carry; carry = sum / 10; tail.next = new ListNode(sum % 10); tail = tail.next; l1 = l1?.next; l2 = l2?.next; } return dummy.next; } }